          orient: 'vertical',
          left: 10,
          data: ['男', '女']
        },
        series: [{
          name: title1 + '级性别比例',
          type: 'pie',
          radius: ['50%', '70%'],
          avoidLabelOverlap: false,
          label: {
            normal: {
              show: false,
              position: 'center'
            },
            emphasis: {
              show: true,
              textStyle: {
                fontSize: '30',
                fontWeight: 'bold'
              }
            }
