    drawNationChart: function drawNationChart() {
      var _this2 = this;

      var myChart = this.$echarts.init(document.getElementById("nationchart"));
      myChart.showLoading();
      this.$http.get("overview/nation").then(function (_ref2) {
        var data = _ref2.data;
        _this2.nationseries = [];
        var glists = Object.keys(data); // console.log(glists);

        var _loop = function _loop(i) {
          var resdata = [];
          var nationname = Object.keys(data[glists[i]]);
          nationname.map(function (nname) {
            resdata.push({
              name: nname,
              value: data[glists[i]][nname]
            });
          }); // console.log(resdata);

          _this2.nationseries.push({
            name: glists[i] == '0' ? '全学院' : glists[i],
            type: 'treemap',
            breadcrumb: {
              show: false
            },
            data: resdata
          });
        };

        for (var i = 0; i < glists.length; i++) {
          _loop(i);
        }

        var option = {
          tooltip: {},
          title: {
            top: 5,
            left: 'center',
            text: '民族分布',
            borderRadius: [5, 5, 0, 0]
          },
          legend: {
            data: _this2.gradelist,
            selectedMode: 'single',
            bottom: 0,
            itemGap: 2,
            type: 'scroll' //设置legend滚动轴的出现

          },
          series: _this2.nationseries
        };
        myChart.hideLoading();
        myChart.setOption(option);
      }).catch(function (err) {
        console.log(err);

        _this2.autoopen('错误', '网络错误,访问超时！');
      });
    },

    /**
     * 年级人数统计图表
     */
    drawGradeNumChart: function drawGradeNumChart() {
      var _this3 = this;

      var gradedata = [];
      var myChart = this.$echarts.init(document.getElementById("gradecountchart"));
      myChart.showLoading();
      this.$http.get("overview/grades").then(function (_ref3) {
        var data = _ref3.data;

        for (var i = 0; i < Object.keys(data).length; i++) {
          gradedata.push({
            name: Object.keys(data)[i],
            value: Object.values(data)[i]
          });
        }

        var option = {
          tooltip: {
            trigger: 'item',
            formatter: '{a} <br/>{b} : {c} ({d}%)'
          },
          visualMap: {
            show: false,
            min: 700,
            max: 900,
            inRange: {
              colorLightness: [0, 1]
            }
          },
          series: [{
            name: '年级人数统计',
            type: 'pie',
            radius: '70%',
            center: ['50%', '50%'],
            data: gradedata.sort(function (a, b) {
              return a.value - b.value;
            }),
            roseType: 'radius'
          }]
        };
        myChart.hideLoading();
        myChart.setOption(option);
      }).catch(function (err) {
        console.log(err);

        _this3.autoopen('错误', '网络错误,访问超时！');
      });
    },

    /**
     * 政治面貌统计
