    drawPoliticalStatusChart: function drawPoliticalStatusChart() {
      var _this4 = this;

      var myChart = this.$echarts.init(document.getElementById("politicalstatuschart"));
      myChart.showLoading();
      this.$http.get("overview/ps").then(function (_ref4) {
        var data = _ref4.data;
        var psseries = []; //初始化图表列表

        var datalist = {}; //初始化图表数据

        var map = {};
        var dest = []; //根据termYear,psname排序

        var sortdata = data.sort(_this4.$sortByArr(["termYear", "psname"])); //根据termYear将json分组

        sortdata.forEach(function (i) {
          if (i.termYear == null) i.termYear = '全学院';

          if (!map[i.psname]) {
            dest.push({
              psname: i.psname,
              content: [i]
            });
            map[i.psname] = "existed";
          } else {
            dest.forEach(function (j) {
              if (j.psname == i.psname) {
                j.content.push(i);
              }
            });
          }
        }); // console.log(map);
        // console.log(datalist);
        // console.log(dest);

        dest.forEach(function (d) {
          //初始化数据集合									
          _this4.gradelist.forEach(function (g) {
            return datalist[g] = 0;
          });

          d['content'].forEach(function (c) {
            datalist[c.termYear] = c.counts;
          });
          psseries.push({
            name: d.psname,
            type: 'bar',
            stack: '总量',
            label: {
              show: false,
              position: 'insideRight'
            },
            data: Object.values(datalist)
          });
        });
        var option = {
          tooltip: {
            trigger: 'axis',
            axisPointer: {
              type: 'shadow'
            }
          },
          legend: {
            type: 'scroll',
            // 设置legend滚动轴的出现
            data: ['党员', '共青团员', '积极分子', '群众', '预备党员']
