          grid: {
            left: '3%',
            right: '4%',
            bottom: '3%',
            containLabel: true
          },
          xAxis: {
            type: 'value'
          },
          yAxis: {
            type: 'category',
            data: Object.keys(datalist)
          },
          series: psseries
        };
        myChart.hideLoading();
        myChart.setOption(option);
      }).catch(function (err) {
        console.log(err);

        _this4.autoopen('错误', '网络错误,访问超时！');
      });
    },

